mapper.select:

from sky_position
where 
    org_id = #{orgId}
	and parent_id = #{orgId}
	and position_model = #{positionModel}


from sky_position
where 
	position_id = #{positionId}
	and org_id=#{orgId}


from sky_position 
where org_id = #{orgId} and position_type = #{positionType} and position_model = #{positionModel}
order by gmt_create


from sky_position 
where org_id = #{orgId} and position_model = #{positionModel}

and parent_id = #{parentId}


and parent_id = 0

order by gmt_create

select position_id
from sky_position 
where org_id = #{orgId} and position_model = #{positionModel}

and parent_id = #{parentId}


and parent_id = 0

order by gmt_create
