mapper.select:

from hive_sub_org
where 
    org_id = #{orgId}
	and parent_id = #{orgId}
	and position_model = #{positionModel}


from hive_sub_org
where 
	sub_org_id = #{positionId}
	and org_id=#{orgId}


from hive_sub_org 
where org_id = #{orgId} and sub_org_type = #{positionType} and position_model = #{positionModel}
order by gmt_create


from hive_sub_org 
where org_id = #{orgId} and position_model = #{positionModel}

and parent_id = #{parentId}


and parent_id = 0

order by gmt_create

select sub_org_id
from hive_sub_org 
where org_id = #{orgId} and position_model = #{positionModel}

and parent_id = #{parentId}


and parent_id = 0

order by gmt_create
